INSERT INTO `wced_entry` VALUES (3839, "da", "186", "<entry page=""186"" id=""3839""><form id=""daq"" lang=""ceb"">dà</form>
<hom>
<sense n=""1""><number>1</number><trans> <ix>particle at end of a clause:</ix> </trans>
</sense>
<sense n=""1a""><number>1a</number><trans> used upon <tr>noticing</tr> s.t. new, <tr>remembering</tr> s.t. one had forgotten. </trans>
<eg><i lang=""ceb"">Mu ra kug gitugnaw dà,</i><trans> I feel cold! </trans></eg>
<eg><i lang=""ceb"">Grábi ning bátà dà,</i><trans> I notice the child is in serious condition. </trans></eg>
<eg><i lang=""ceb"">Lamían tung ímung sud-an dà!</i><trans> Your food was delicious! </trans></eg>
</sense>
<sense n=""1b""><number>1b</number><trans> <ix>particle of apology for doing s.t. poorly, that one was pressed into doing:</ix> don’t <tr>blame</tr> me if things don’t work out right. </trans>
<eg><i lang=""ceb"">Dì ra ba ku kamau nga mubayli dà,</i><trans> I don’t know how to dance. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> <ix>initial in a clause:</ix> there, I <tr>told you so</tr>! </trans>
<eg><i lang=""ceb"">Dà, nasámad ka hinúun,</i><trans> There, now you have hurt yourself.
</trans></eg>
</sense></hom></entry>");
