INSERT INTO `wced_entry` VALUES (3892, "dagpi", "189", "<entry page=""189"" id=""3892""><form id=""dagpiq"" lang=""ceb"">dagpì</form>
<hom>
<sense n=""0""><trans> (from <ix>dapì</ix>) </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AN; ab2]</itype><trans> <tr>slap</tr> with the palm of the hand. </trans>
<eg><i lang=""ceb"">May kamut nga midagpì (nanagpì) sa ákung áping,</i><trans> A hand slapped my cheek. </trans></eg>
