INSERT INTO `wced_entry` VALUES (3896, "dagtum", "190", "<entry page=""190"" id=""3896""><form id=""dagtum"" lang=""ceb"">dagtum</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>dark grey</tr>, <tr>gloomy</tr>. </trans>
<eg><i lang=""ceb"">Ang dagtum sa lángit nagtilimad-un ug ulan,</i><trans> Dark skies are a sign of rain. </trans></eg>
