INSERT INTO `wced_entry` VALUES (3967, "dalag1", "195", "<entry page=""195"" id=""3967""><form id=""dalag"" lang=""ceb"">dalag<sub>1</sub></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>light yellow</tr>. </trans>
<eg><i lang=""ceb"">Dalag ang íhì,</i><trans> Urine is yellow. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> yellowed or brown fallen <tr>leaves</tr>. </trans>
<eg><i lang=""ceb"">Dalag sa lumbuy himúun ug líkin,</i><trans> Yellowed <ix>lumbuy</ix> leaves are made into cigars. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
