INSERT INTO `wced_entry` VALUES (4137, "dapat", "203", "<entry page=""203"" id=""4137""><form id=""daxpat"" lang=""ceb"">dápat</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A2SC; ac]</itype><trans> bring s.t. into contact with s.t. else. </trans>
<eg><i lang=""ceb"">Nagdapat sa yútà,</i><trans> Touching the ground. </trans></eg>
<eg><i lang=""ceb"">Dapátun ta ang duha ta ka kamut,</i><trans> Let’s let our hands touch each other. </trans></eg>
<eg><i lang=""ceb"">Idápat ang ímung tiil sa sanga,</i><trans> Let your feet touch the branch. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; c]</itype><trans> <tr>administer</tr> medicine. </trans>
<eg><i lang=""ceb"">Nahikatúlug siya human kadapátig tambal,</i><trans> He fell asleep after the medicine had been administered to him. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; b(1)]</itype><trans> lay hands on one, <tr>hit</tr>. </trans>
<eg><i lang=""ceb"">Anad giyud nà siyang mudápat sa íyang asáwa,</i><trans> He is used to hitting his wife. </trans></eg>
</sense>
<sense n=""4""><number>4</number> <itype>[A; c]</itype><trans> use in building s.t. </trans>
<eg><i lang=""ceb"">Dì ku mudápat ug lawáan sa salug,</i><trans> I won’t use lauan wood for the floor. </trans></eg>
<eg><i lang=""ceb"">Ang balay gidapátan ug kawáyan,</i><trans> The house was built of bamboo. </trans></eg>
</sense>
<sense n=""5""><number>5</number> <itype>[c]</itype><trans> <tr>money</tr> spent on. </trans>
<eg><i lang=""ceb"">Unsay ímung gidapátan sa ímung kwarta?</i><trans> What have you done with your money? a suitable, fitting (used by speakers influenced by Tagalog). </trans></eg>
<eg><i lang=""ceb"">Dì dápat kanang kulúra nímu,</i><trans> That is not a good color for you. </trans></eg>
<eg><i lang=""ceb"">Dílì nà dápat pagabuhátun,</i><trans> You shouldn’t do that. </trans></eg>
</sense></hom>
<entry><form id=""walay_daxpat"" lang=""ceb"">walay <abbr expan=""dápat"" type=""lemma"">—</abbr></form>
<hom>
<sense n=""0""><trans> useless, inapplicable. </trans>
