INSERT INTO `wced_entry` VALUES (4325, "digu", "214", "<entry page=""214"" id=""4325""><form id=""dixguq"" lang=""ceb"">dígù</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a]</itype><trans> bathe s.o. </trans>
<eg><i lang=""ceb"">Nakadígù ka na ba sa irù?</i><trans> Have you bathed the dog? </trans></eg>
