INSERT INTO `wced_entry` VALUES (4333, "dihadu", "214", "<entry page=""214"" id=""4333""><form id=""dihaxdu"" lang=""ceb"">dihádu</form>
<hom>
<sense n=""1""><number>1</number><trans> negligent, careless of one’s things. </trans>
