INSERT INTO `wced_entry` VALUES (4482, "dirikta", "220", "<entry page=""220"" id=""4482""><form id=""dirikta"" lang=""ceb"">dirikta</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>frank</tr>, speaking to the point. </trans>
</sense>
<sense n=""2""><number>2</number><trans> be <pb n=""221""/>directly from, go directly to. </trans>
<eg><i lang=""ceb"">Radiyu nga ismágul dirikta gíkan sa Hapun,</i><trans> Radios, smuggled directly from Japan. </trans></eg>
