INSERT INTO `wced_entry` VALUES (4639, "diyut", "227", "<entry page=""227"" id=""4639""><form id=""diyut"" lang=""ceb"">diyut</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>small</tr> in size. </trans>
