INSERT INTO `wced_entry` VALUES (4689, "duga, duga", "229", "<entry page=""229"" id=""4689""><form id=""dugaq_duxgaq"" lang=""ceb"">dugà, dúgà</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> juice, <tr>sap</tr> from a plant, juice coming from inside meat. </trans>
<eg><i lang=""ceb"">Dugà sa agridulsi,</i><trans> Calomondin juice. </trans></eg>
<eg><i lang=""ceb"">Ang dugà sa alipáta makabúta,</i><trans> The sap from the <ix>alipáta</ix> tree can blind you. </trans></eg>
<eg><i lang=""ceb"">Karning nalútù sa íyang dúgà,</i><trans> Meat cooked in its own juices. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> oozings from infections. </trans>
</sense>
<sense n=""3""><number>3</number><trans> semen (humorous euphemism). </trans>
<eg><i lang=""ceb"">Wà nay dugang tigúlang,</i><trans> An old man with no more sperm. </trans></eg>
</sense>
<sense n=""4""><number>4</number><trans> a man’s children (humorous). </trans>
<eg><i lang=""ceb"">Kinsay mubuhì sa ímung dugà kun patay ka na?</i><trans> Who will raise your children when you are gone? </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A3P]</itype><trans> exude sap or juice. </trans>
<eg><i lang=""ceb"">Nakadugà na ba ang hubag?</i><trans> Has any pus come out of the boil? </trans></eg>
</sense></hom>
<entry><form id=""dugaun"" lang=""ceb""><abbr expan=""dugaun"" type=""lemma"">-un(→)</abbr></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> juicy.
