INSERT INTO `wced_entry` VALUES (4692, "dugang", "229", "<entry page=""229"" id=""4692""><form id=""duxgang"" lang=""ceb"">dúgang</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[AB2S4; c]</itype><trans> add to s.t., <tr>increase</tr> in amount. </trans>
