INSERT INTO `wced_entry` VALUES (4737, "dukiduki, dukiduki", "233", "<entry page=""233"" id=""4737""><form id=""dukiqdukiq_dukiqduxkiq"" lang=""ceb"">dukìdukì, dukìdúkì</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[AN; a12]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> ransack s.t. in search of s.t. </trans>
<eg><i lang=""ceb"">Nanukìdukì ang makililímus sa mga basúra kun unsay makit-an,</i><trans> The beggar ransacked the garbage can to see what he could find. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number><trans> investigate, question in detail. </trans>
<eg><i lang=""ceb"">Ang syinsiya nagdukìdukì sa makaingun sa kansir,</i><trans> Science is searching for the cause of cancer. </trans></eg>
<eg><i lang=""ceb"">Gidukìdúkì siya sa pulis,</i><trans> The police questioned him in detail. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> tinker with. </trans>
