INSERT INTO `wced_entry` VALUES (5888, "halin", "292", "<entry page=""292"" id=""5888""><form id=""haxlin"" lang=""ceb"">hálin</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; ac]</itype><trans> sell s.t. at a certain price. </trans>
<eg><i lang=""ceb"">Ug halínun mug míl,</i><trans> If you sell it for a thousand. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B2]</itype><trans> for s.t. to sell. </trans>
<eg><i lang=""ceb"">Ang ákung yútà muhálin na karun ug labaw pa sa dubli,</i><trans> My lot will sell for more than double the buying price. </trans></eg>
