INSERT INTO `wced_entry` VALUES (5942, "hambug", "295", "<entry page=""295"" id=""5942""><form id=""hambug"" lang=""ceb"">hambug</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> big talk, most often not quite true. </trans>
