INSERT INTO `wced_entry` VALUES (5983, "hana", "297", "<entry page=""297"" id=""5983""><form id=""hana"" lang=""ceb"">hana</form>
<hom>
<sense n=""0""><trans> (from <ix>hanu na</ix>) </trans>
</sense>
<sense n=""1""><number>1</number><trans> how about it? how is it now? </trans>
<eg><i lang=""ceb"">Hana? Wà ka pa mahubug? Ganína ka ra mang inum<corr sic=""."">,</corr></i><trans> How about it? Aren’t you drunk yet? You’ve been drinking for hours. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> there, that’s what you get. </trans>
