INSERT INTO `wced_entry` VALUES (5987, "hanap", "297", "<entry page=""297"" id=""5987""><form id=""hanap"" lang=""ceb"">hanap</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>hazy</tr>, difficult to discern. </trans>
<eg><i lang=""ceb"">Hanap ang mga litra,</i><trans> The letters are very faint (or blurred). </trans></eg>
<eg><i lang=""ceb"">Hanap ang kaháyag sa bitúun,</i><trans> The <pb n=""298""/>star gives off a faint light. </trans></eg>
<eg><i lang=""ceb"">Hanap nga pahíyum,</i><trans> A faint smile. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number><trans> for s.t. in relief to be so worn that it can hardly be distinguished. </trans>
<eg><i lang=""ceb"">Hanap nga dyís,</i><trans> A worn-out dime. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> <tr>vague</tr>, uncertain. </trans>
<eg><i lang=""ceb"">Hanap nga kaugmáun,</i><trans> An uncertain future. </trans></eg>
<eg><i lang=""ceb"">Hanap nga pagtúu sa Diyus,</i><trans> A not very strong belief in God. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> for one’s vision to be dim. </trans>
<eg><i lang=""ceb"">Hanap ákung panan-aw,</i><trans> My eyes are dim. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B; a]</itype><trans> become faint, blurred, do s.t. in a blurred way. </trans>
<eg><i lang=""ceb"">Muhanap ang ági sa lápis ug dílì nímu iduut,</i><trans> The pencil makes a very weak mark if you don’t press down on it. </trans></eg>
<eg><i lang=""ceb"">Ang naghanap níyang mga sáad,</i><trans> His uncertain promises.
</trans></eg>
</sense></hom></entry>");
