INSERT INTO `wced_entry` VALUES (6012, "handum", "299", "<entry page=""299"" id=""6012""><form id=""handum"" lang=""ceb"">handum</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; a12]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> recall and think fondly about s.t. memorable, but forever past. </trans>
<eg><i lang=""ceb"">Naghandum ku sa ákung kabatan-un,</i><trans> I remember my youth. </trans></eg>
<eg><i lang=""ceb"">Handúmun ku ikaw kanúnay,</i><trans> I shall always think of you and remember you with fondness. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> recall and yearn for s.t. </trans>
