INSERT INTO `wced_entry` VALUES (6049, "hangup", "302", "<entry page=""302"" id=""6049""><form id=""hangup"" lang=""ceb"">hangup</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A2C; a12]</itype><trans> <tr>embrace</tr> or <tr>greet</tr> with joy, esp. upon arriving. </trans>
<eg><i lang=""ceb"">Muhangup ang bátà nákù inig-abut nákù sa balay,</i><trans> The child greets me with joy as soon as I get home. </trans></eg>
<eg><i lang=""ceb"">Naghángup dáyun sila sa ílang panagkítà,</i><trans> They immediately rushed into each other’s arms when they saw each other. </trans></eg>
<eg><i lang=""ceb"">Ayaw ku hangpa kay gisip-un ku,</i><trans> Don’t embrace me. I have a cold. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <corr sic=""[""/><form id=""gihangup"" lang=""ceb""><abbr expan=""gihangup"" type=""lemma"">gi-</abbr></form><trans><corr sic=""[""/> be affected by the sickness <ix>hangup.</ix> </trans>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> ailment brought on by the spirit of a deceased relative whom one failed to remember with a prayer, mass, or the like. </trans>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> affectionate in one’s greeting. </trans>
<eg><i lang=""ceb"">Kinasingkásing ug hangup gayud nga pagdáwat,</i><trans> Sincere and joyous reception. </trans></eg>
</sense></hom>
<entry><form id=""hinangup"" lang=""ceb""><abbr expan=""hinangup"" type=""lemma"">-in-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B126; c5]</itype><trans> be <tr>pleased</tr> by some action. </trans>
<eg><i lang=""ceb"">Nahinangup aku sa íyang gibúhat,</i><trans> What he did pleased me. </trans></eg>
<eg><i lang=""ceb"">Gikahinangup námù ang ímung sulat,</i><trans> Your letter pleased us. </trans></eg>
</sense></hom></entry>
<entry><form id=""kahinangup"" lang=""ceb"">kahinangup</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> pleasure at some action. </trans>
</sense></hom></entry>
<entry><form id=""hinalangpun"" lang=""ceb"">hinalangpun</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>welcome</tr>. </trans>
<eg><i lang=""ceb"">Ang balintayins dí mauy usa ka hinalangpun nga higayun,</i><trans> Valentine’s Day is a welcome occasion. </trans></eg>
</sense></hom></entry>
<entry><form id=""mahinalangpun_mahinangpaxnun"" lang=""ceb"">mahinalangpun, mahinangpánun</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>with pleasure</tr>, <tr>enthusiasm</tr>. </trans>
<eg><i lang=""ceb"">Mahinalangpun kaáyu siyang miúyun sa plánu,</i><trans> He agreed to the plan enthusiastically.
</trans></eg>
</sense></hom></entry></entry>");
