INSERT INTO `wced_entry` VALUES (6068, "hantak", "303", "<entry page=""303"" id=""6068""><form id=""hantak"" lang=""ceb"">hantak</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> gambling game played with three of the old, large one-centavo coins which are tossed down simultaneously on a stone (<ix>antugan</ix>). The one who tosses bets all heads against s.o. who bets all tails. Bystanders may join in. </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[AC; b6]</itype><trans> play <ix>hantak.</ix> </trans>
</sense></hom>
