INSERT INTO `wced_entry` VALUES (6094, "hapay", "305", "<entry page=""305"" id=""6094""><form id=""haxpay"" lang=""ceb"">hápay</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[B126]</itype><trans> for a thick cluster of s.t. long and standing to fall or be bent at its base. </trans>
<eg><i lang=""ceb"">Nahápay ang mga humay,</i><trans> The rice plants were bent over. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; a1]</itype><trans> comb s.o.’s hair flat to the head. </trans>
<eg><i lang=""ceb"">Díay pamáda. Hapáya ang íyang buhuk,</i><trans> Here’s some pomade. Comb his hair flat. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[B126]</itype><trans> go bankrupt. </trans>
<eg><i lang=""ceb"">Nahápay ang ílang nigusyu kay gigastu man hasta puhúnan,</i><trans> Their business went bankrupt because he spent every penny of it, even the capital. </trans></eg>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> bent over flat. </trans>
</sense>
<sense n=""2""><number>2</number><trans> for a man’s hair to be neatly groomed flat on the head. </trans>
</sense></hom>
<entry><form id=""hinaxpay"" lang=""ceb""><abbr expan=""hinápay"" type=""lemma"">-in-</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> man’s combed hair. </trans>
