INSERT INTO `wced_entry` VALUES (6099, "hapin", "305", "<entry page=""305"" id=""6099""><form id=""hapin"" lang=""ceb"">hapin</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> s.t. that is laid over or under s.t. </trans>
<eg><i lang=""ceb"">Hapin sa lamísa,</i><trans> Tablecloth. </trans></eg>
<eg><i lang=""ceb"">Hapin sa bátà,</i><trans> Baby’s diaper. </trans></eg>
<eg><i lang=""ceb"">Hapin sa piryudiku,</i><trans> The magazine cover. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> food taken along with drinks as a digestive buffer (slang). </trans>
<eg><i lang=""ceb"">Way ulsir basta dúnay hapin inig-ínum,</i><trans> You won’t get ulcers if you take in some food before a drinking spree. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b2c1]</itype><trans> lay s.t. over s.t. </trans>
<eg><i lang=""ceb"">Hapinun (ihapin) ku ning dáhun sa ságing sa baskit,</i><trans> I will line the bottom of the basket with this banana leaf. </trans></eg>
<eg><i lang=""ceb"">Gihapinan nákù ang katri ug putì nga hábul,</i><trans> I covered the bed with a white sheet. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; b6(1)]</itype><trans> take in food as a buffer.
</trans>
</sense></hom></entry>");
