INSERT INTO `wced_entry` VALUES (6100, "hapit", "305", "<entry page=""305"" id=""6100""><form id=""hapit"" lang=""ceb"">hapit</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A2; b6(1)]</itype><trans> <tr>drop by</tr>, <tr>stop in</tr> for a short while. </trans>
<eg><i lang=""ceb"">Hapit úsà mu sa balay,</i><trans> Stop in the house for a while. </trans></eg>
<eg><i lang=""ceb"">Ang ayruplánu mihapit úsà sa Haway,</i><trans> Their plane made a stopover in Hawaii. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; ac]</itype><trans> get or bring s.t. on the way. </trans>
<eg><i lang=""ceb"">Akuy muhapit níya sa íla,</i><trans> I will stop in her house and fetch her on the way. </trans></eg>
<eg><i lang=""ceb"">Kinsay maghapit sa libru sa ámù?</i><trans> Who will stop by our place to deliver (or get) the book? </trans></eg>
