INSERT INTO `wced_entry` VALUES (6182, "hasul", "310", "<entry page=""310"" id=""6182""><form id=""haxsul"" lang=""ceb"">hásul</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a]</itype><trans> <tr>bother</tr>, <tr>give trouble</tr>, <tr>inconvenience</tr>. </trans>
<eg><i lang=""ceb"">Muhásul ku nímug kadiyut ha?</i><trans> May I bother you for a moment? </trans></eg>
<eg><i lang=""ceb"">Gihásul ku pag-áyu niíning sip-un,</i><trans> I am very much bothered by my cold. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A3; a2]</itype><trans> bother to do, take the trouble of doing s.t. </trans>
<eg><i lang=""ceb"">Naghásul ka pa mag dala ug ságing níay daghan,</i><trans> You have taken the trouble of bringing bananas when we have so many! </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A13]</itype><trans> for a child to be fussy. </trans>
<eg><i lang=""ceb"">Naghásul ang bátà kay gibutdag tiyan,</i><trans> The child is fussy because he has an upset stomach. </trans></eg>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> entailing a lot of annoyance, bother. </trans>
<eg><i lang=""ceb"">Hásul kaáyung adtúun, <pb n=""311""/>daghang sakaysákay,</i><trans> It’s a lot of trouble to go there because there’s no direct transportation. </trans></eg>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> being a lot of trouble, causing inconvenience. </trans>
</sense></hom>
<hom role=""n""><pos>n</pos></hom>
<entry><form id=""haxsul_sa_katursi"" lang=""ceb""><abbr expan=""hásul"" type=""lemma"">—</abbr> sa katursi</form>
<hom>
<sense n=""1""><number>1</number><trans> World War I. </trans>
</sense>
<sense n=""2""><number>2</number><trans> causing very much trouble and bother. </trans>
<eg><i lang=""ceb"">Hásul sa katursi ning batáa,</i><trans> This child is a terrible nuisance. </trans></eg>
</sense></hom></entry>
<entry><form id=""kahaxsul"" lang=""ceb""><abbr expan=""kahásul"" type=""lemma"">ka-</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>trouble</tr>, <tr>annoyance</tr>. </trans>
</sense></hom></entry>
<entry><form id=""mahasuxlun"" lang=""ceb""><abbr expan=""mahasúlun"" type=""lemma"">ma-un</abbr></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> causing lots of trouble and inconvenience. </trans>
<eg><i lang=""ceb"">Mahasúlung mga hangyù,</i><trans> Requests that entail a lot of trouble.
</trans></eg>
</sense></hom></entry></entry>");
