INSERT INTO `wced_entry` VALUES (6219, "hawid, hawid1", "314", "<entry page=""314"" id=""6219""><form id=""hawid_haxwid"" lang=""ceb"">hawid, háwid<sub>1</sub></form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>similar</tr>, <tr>resembling</tr>. </trans>
<eg><i lang=""ceb"">Mas hawid sa amahan ang kamagulángan,</i><trans> The eldest is most similar in appearance to his father. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A23; a]</itype><trans> resemble, be similar. </trans>
