INSERT INTO `wced_entry` VALUES (6336, "hiklin, hikling", "321", "<entry page=""321"" id=""6336""><form id=""hiklin_hikling"" lang=""ceb"">hiklin, hikling</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AB26; c]</itype><trans> put s.t. aside and out of the way, <tr>step aside</tr>. </trans>
<eg><i lang=""ceb"">Ang mga napárut mihikling lang sa daplin,</i><trans> The losers stepped aside. </trans></eg>
<eg><i lang=""ceb"">Ihiklin ang síya sa iskína,</i><trans> Put this chair away in the corner. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A2; b(1)]</itype><trans> <tr>take refuge</tr>, <tr>hide</tr>. </trans>
<eg><i lang=""ceb"">Swirting nakahiklin siya luyu sa káhuy,</i><trans> Luckily he was able to hide behind the tree. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; c]</itype><trans> <tr>save</tr>, <tr>set aside</tr>. </trans>
<eg><i lang=""ceb"">Maghiklin tag kwarta pára sa átung katigulangun,</i><trans> We should set aside some amount for our old <corr sic=""áge"">age</corr>. </trans></eg>
<eg><i lang=""ceb"">Dúna siyay gihikling nga pagkáun pára nímu,</i><trans> She has put some food aside for you. </trans></eg>
</sense>
<sense n=""4""><number>4</number><trans> transfer one’s residence to escape s.t. </trans>
<eg><i lang=""ceb"">Mihiklin mi pagkasúnug sa ámung balay,</i><trans> We moved away when our house burnt down.
</trans></eg>
