INSERT INTO `wced_entry` VALUES (6446, "hinam", "328", "<entry page=""328"" id=""6446""><form id=""hixnam"" lang=""ceb"">hínam</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B126; b3(1)]</itype><trans> be eager, intensely desirous. </trans>
