INSERT INTO `wced_entry` VALUES (6455, "hinaw", "328", "<entry page=""328"" id=""6455""><form id=""hinaw"" lang=""ceb"">hinaw</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A2; b]</itype><trans> wash the hands. </trans>
