INSERT INTO `wced_entry` VALUES (6617, "hubug", "338", "<entry page=""338"" id=""6617""><form id=""hubug"" lang=""ceb"">hubug</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> <tr>drunk</tr>, <tr>intoxicated</tr>. </trans>
<eg><i lang=""ceb"">Hubug kaáyu ang táwu kay nagsusapinday na,</i><trans> The man must be very drunk. He is staggering. </trans></eg>
<eg><i lang=""ceb"">Hubug sa kalípay,</i><trans> Intoxicated with happiness. </trans></eg>
<eg><i lang=""ceb"">Hubug sa báhù,</i><trans> Whoozy from the stench. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A3P; a]</itype><trans> make drunk. </trans>
<eg><i lang=""ceb"">Hubgun ku si Pípi,</i><trans> I’ll get Pepe drunk. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number> <itype>[A13]</itype><trans> make oneself drunk. </trans>
<eg><i lang=""ceb"">Maghubug ku arun malímut,</i><trans> I will get drunk in order to forget. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B126]</itype><trans> be <tr>groggy</tr> from some outside cause. </trans>
<eg><i lang=""ceb"">Nahubug ang buksidur sa nagpungasing kúmù sa kuntra,</i><trans> The boxer became groggy from the blows his opponent rained on him. </trans></eg>
<eg><i lang=""ceb"">Ang anghit nímu makahubug,</i><trans> Your stench can make one pass out. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A23PB126; a1]</itype><trans> <tr>excite</tr> greatly, overcome with powerful emotion. </trans>
<eg><i lang=""ceb"">Nahubug siya sa gahum nga gisángun kaníya,</i><trans> He was intoxicated by the power entrusted to him. </trans></eg>
