INSERT INTO `wced_entry` VALUES (6658, "hugpa", "341", "<entry page=""341"" id=""6658""><form id=""hugpaq"" lang=""ceb"">hugpà</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b6]</itype><trans> alight in a swarm. </trans>
<eg><i lang=""ceb"">Mihugpà ang dúlun sa maisan,</i><trans> The locusts alighted on the cornfield in a swarm. </trans></eg>
