INSERT INTO `wced_entry` VALUES (6668, "hugus", "341", "<entry page=""341"" id=""6668""><form id=""huxgus"" lang=""ceb"">húgus</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; a1]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> raise or lower s.t. with a string or rope over a stick or pulley. </trans>
<eg><i lang=""ceb"">Siyay mihúgus sa bandíra,</i><trans> He raised the flag. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> pull pursing strings. </trans>
<eg><i lang=""ceb"">Hugúsa ang písì pagsira sa bág,</i><trans> Pull the purse strings to close the bag. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> action of raising or lowering s.t. carefully or on ropes. </trans>
</sense>
<sense n=""2""><number>2</number><trans> name given to the <ix>súgat</ix> ceremony of Easter Sunday, so called because at the point that Christ and the Holy Mother meet, angels (<ix>alilúya</ix>) are lowered. </trans>
</sense>
<sense n=""3""><number>3</number><trans> the name given to the procession in the evening of Good Friday which represents the carrying of the body of Christ to the grave. </trans>
</sense></hom>
<entry><form id=""hugus"" lang=""ceb""><abbr expan=""hugus"" type=""lemma"">(→)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> string used for raising or moving s.t. </trans>
<eg><i lang=""ceb"">Hugus sa tilun,</i><trans> The curtain string. </trans></eg>
</sense></hom></entry>
<entry><form id=""hugushuxgus"" lang=""ceb"">hugushúgus</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> s.t. manipulated by pulleys or drawstrings.
