INSERT INTO `wced_entry` VALUES (6793, "hunahuna", "350", "<entry page=""350"" id=""6793""><form id=""hunaqhuxnaq"" lang=""ceb"">hunàhúnà</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A3S; a2b2]</itype><trans> <tr>think about</tr> s.t. </trans>
<eg><i lang=""ceb"">Ug hunàhunáun tug maáyu, sayup giyud tu,</i><trans> If you think about it, it was a grave mistake. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A3S; b8]</itype><trans> think of doing s.t. </trans>
<eg><i lang=""ceb"">Walà ka ba makahunàhunà ug hulam?</i><trans> Have you not thought of borrowing some? </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A3; a12]</itype><trans> think of s.t. or s.o., <tr>consider</tr>. </trans>
<eg><i lang=""ceb"">Hunàhunáa ang ímung mga bátà unsay dangátan níla,</i><trans> Think of what will happen to your children. </trans></eg>
</sense>
<sense n=""4""><number>4</number> <itype>[a12]</itype><trans> think s.t. over, <tr>ponder</tr>. </trans>
<eg><i lang=""ceb"">Ákù sang hunàhunáun. Dì úsà tikaw tubagun,</i><trans> I’ll think it over. I won’t give you an answer yet. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>belief</tr>, <tr>opinion</tr>. </trans>
<eg><i lang=""ceb"">Unsa may ímung hunàhúnà (panghunàhunà) báhin ánà?</i><trans> What is your opinion about that? </trans></eg>
</sense>
<sense n=""1a""><number>1a</number><trans> <tr>decision</tr>. </trans>
<eg><i lang=""ceb"">Dì giyud mausab ang ímung hunàhúnà?</i><trans> Is your decision completely irreversible? </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> <tr>thought</tr>, <tr>idea</tr>. </trans>
<eg><i lang=""ceb"">Púru mga hunàhúnà (panghunàhunà) nga bastus ang náa sa íyang úlu,</i><trans> He has nothing but indecent thoughts in his head. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> thoughts regarding s.o., <tr>attitude</tr>. </trans>
<eg><i lang=""ceb"">Wà kuy maáyung hunàhúnà níya human sa íyang gibúhat,</i><trans> I don’t have a good attitude towards him after what he did. </trans></eg>
</sense></hom>
<entry><form id=""panghunaqhunaq"" lang=""ceb""><abbr expan=""panghunàhunà"" type=""lemma"">paN-(→)</abbr></form>
<hom role=""n""><pos>n</pos>
