INSERT INTO `wced_entry` VALUES (6891, "hustu", "356", "<entry page=""356"" id=""6891""><form id=""hustu"" lang=""ceb"">hustu</form>
<hom>
<sense n=""1""><number>1</number><trans> proper in time. </trans>
<eg><i lang=""ceb"">Hustu na rung ilarga, Now is a good time to leave.</i></eg>
</sense>
<sense n=""2""><number>2</number><trans> <tr>enough</tr>, <tr>sufficient</tr>. </trans>
<eg><i lang=""ceb"">Duha ka buuk pán hustu pára pugung sa gútum,</i><trans> Two pieces of bread are enough to stave off hunger. </trans></eg>
<eg><i lang=""ceb"">Hustu sa kaínit ang kapi,</i><trans> The coffee is just hot enough. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> <tr>fitting</tr>. </trans>
<eg><i lang=""ceb"">Hustu ba ang sukud?</i><trans> Is it the right size? </trans></eg>
</sense>
<sense n=""4""><number>4</number><trans> <tr>right</tr>, <tr>correct</tr>. </trans>
<eg><i lang=""ceb"">Hustu ang ímung tubag,</i><trans> You gave the right answer. </trans></eg>
</sense>
<sense n=""5""><number>5</number><trans> having reached exactly [so-and-so] much. </trans>
<eg><i lang=""ceb"">Hustu nang usa ka dusína,</i><trans> This makes it exactly one dozen. </trans></eg>
</sense>
<sense n=""5a""><number>5a</number><trans> be <tr>complete</tr>. </trans>
<eg><i lang=""ceb"">Íning instulmína, hustu na ang báyad,</i><trans> With this installment the payment is complete. </trans></eg>
<eg><i lang=""ceb"">Hustu pa ba ang baráha?</i><trans> Is the deck still complete? </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A2]</itype><trans> <tr>fit</tr>. </trans>
<eg><i lang=""ceb"">Muhustu ba sa ímung tiil ning sapatúsa?</i><trans> Will these shoes fit you? </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B126; a12]</itype><trans> be <tr>correct</tr>. </trans>
</sense>
<sense n=""3""><number>3</number> <itype>[A2; a12]</itype><trans> reach a certain number. </trans>
<eg><i lang=""ceb"">Makapanunud ka ug mahustu ka na sa idad,</i><trans> When you reach legal age, you can claim your inheritance. </trans></eg>
</sense>
<sense n=""3a""><number>3a</number> <itype>[A]</itype><trans> for a debt to be paid off entirely. </trans>
</sense></hom>
<entry><form id=""kahustuxan"" lang=""ceb"">kahustúan</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> completion or arrival at a quantity as an end. </trans>
