INSERT INTO `wced_entry` VALUES (6925, "huut1", "358", "<entry page=""358"" id=""6925""><form id=""huut"" lang=""ceb"">huut<sub>1</sub></form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> tight in fit. </trans>
<eg><i lang=""ceb"">Huut kaáyu ang ákung sapátus,</i><trans> My shoes are very tight. </trans></eg>
<eg><i lang=""ceb"">Huut ang takub. Gáhing tangtángun,</i><trans> The cover is on very tight. It’s hard to get it off. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> tight in feeling. </trans>
<eg><i lang=""ceb"">Huut kaáyu ang ákung tunlan tungud sa kahadluk,</i><trans> My throat was tight with fear. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> <tr>crowded</tr>. </trans>
<eg><i lang=""ceb"">Huut kaáyu ang sinihan,</i><trans> The moviehouse was very crowded. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A23; acP]</itype><trans> stuck or wedged tight. </trans>
<eg><i lang=""ceb"">Ang bábuy mihuut sa imburnal,</i><trans> The pig got stuck in the culvert. </trans></eg>
<eg><i lang=""ceb"">Ihuut ang kálù sa úlu,</i><trans> Put the hat tightly on the head. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number> <itype>[B4]</itype><trans> for the chest, throat to tighten. </trans>
</sense>
<sense n=""2""><number>2</number> <itype>[A2]</itype><trans> fill up a space entirely. </trans>
<eg><i lang=""ceb"">Ang íyang nawung mihuut sa ritrátu,</i><trans> Her face filled up the photograph. </trans></eg>
</sense></hom>
<entry><form id=""huxut"" lang=""ceb""><abbr expan=""húut"" type=""lemma"">(←)</abbr></form>
<hom role=""a""><pos>a</pos>
