INSERT INTO `wced_entry` VALUES (6948, "huyang", "359", "<entry page=""359"" id=""6948""><form id=""huxyang"" lang=""ceb"">húyang</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> rickety, shaky. </trans>
<eg><i lang=""ceb"">Húyang nga hagdan,</i><trans> Rickety staircase. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> lacking in firmness, resolve, or will power. </trans>
<eg><i lang=""ceb"">Húyang siya kaáyu. Dì makabalíbad,</i><trans> He’s weak-willed. He can’t say ‘no’. </trans></eg>
<eg><i lang=""ceb"">Ang táwung húyang húmuk matintal,</i><trans> A weak person easily gets tempted. </trans></eg>
</sense>
<sense n=""3""><number>3</number><trans> weak, not said with conviction or persuasiveness. </trans>
<eg><i lang=""ceb"">Misugut siya sa usa ka húyang nga tangdù,</i><trans> He agreed with a weak ‘yes’. </trans></eg>
<eg><i lang=""ceb"">Húyang kaáyung pangatarúngan,</i><trans> Shaky arguments. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B1; b6]</itype><trans> for s.t. built to be or become rickety, shaky or for s.o.’s will, argument to become weak. </trans>
