INSERT INTO `wced_entry` VALUES (6960, "huyup", "360", "<entry page=""360"" id=""6960""><form id=""huyup"" lang=""ceb"">huyup</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a]</itype><trans> blow air. </trans>
<eg><i lang=""ceb"">Tugnaw basta muhuyup ang amíhan,</i><trans> It’s cold when the northeast wind blows. </trans></eg>
<eg><i lang=""ceb"">Huypa ang ákung samad,</i><trans> Blow on my wound. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A3; a2]</itype><trans> blow s.t. away. </trans>
<eg><i lang=""ceb"">Gihuyup sa hángin ang mga papílis,</i><trans> The wind blew the papers away. </trans></eg>
<eg><i lang=""ceb"">Gihuypan níya ang libru sa abug,</i><trans> He blew the dust off the books. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A2; a]</itype><trans> treat an ailment by <ix>huyup.</ix> </trans>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> action of blowing. </trans>
</sense>
<sense n=""2""><number>2</number><trans> folk treatment of illness whereby the doctor whispers magical words on the ailing portion of the body and blows softly on it three times. </trans>
</sense></hom>
<entry><form id=""huxyup"" lang=""ceb""><abbr expan=""húyup"" type=""lemma"">(←)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> a children’s game of blowing rubber bands with the mouth. </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[AC; a]</itype><trans> play this game. </trans>
</sense></hom></entry>
<entry><form id=""huypanay"" lang=""ceb"">huypanay</form>
<hom>
<sense n=""0""><trans> <xr lang=""ceb"" target=""#huxyup"">= <sc>húyup</sc>.</xr>
</trans>
</sense></hom></entry></entry>");
