INSERT INTO `wced_entry` VALUES (6961, "huyut", "360", "<entry page=""360"" id=""6961""><form id=""huyut"" lang=""ceb"">huyut</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B]</itype><trans> for s.t. to bend in the middle under weight. </trans>
<eg><i lang=""ceb"">Húyang ang salug kay muhuyut kun tumban,</i><trans> The flooring is weak because it sags when you step on it. </trans></eg>
<eg><i lang=""ceb"">Mihuyut (nahuyut) ang kisami,</i><trans> The ceiling sagged in the middle. </trans></eg>
<eg><i lang=""ceb"">Nagkahuyut ang salug nga húyang,</i><trans> Loose floor is becoming wobbly. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[B2]</itype><trans> sink, as objects do in the sand or mud. </trans>
<eg><i lang=""ceb"">Muhuyut (mahuyut) ang ímung tiil kun itunub sa lápuk,</i><trans> Your feet will sink if you step in the mud. </trans></eg>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> tending to sag. </trans>
</sense>
<sense n=""2""><number>2</number><trans> allowing the feet to sink when stepped in.
</trans>
</sense></hom></entry>");
