INSERT INTO `wced_entry` VALUES (10351, "laang", "537a", "<entry page=""537a"" id=""10351""><form id=""laxang"" lang=""ceb"">láang</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>noose</tr> of a trap. </trans>
<eg><i lang=""ceb"">Biraha ang láang inigtunub sa manuk,</i><trans> Pull the noose when the chicken steps in. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number><trans> any trap that has a noose. </trans>
</sense>
<sense n=""2""><number>2</number><trans> stratagem designed to catch or trick s.o. </trans>
<eg><i lang=""ceb"">Babáyi ang gamíta nga láang pagsíkup sa ispíya,</i><trans> Use a woman as a trap to snare the spy. </trans></eg>
