INSERT INTO `wced_entry` VALUES (10352, "laas", "537a", "<entry page=""537a"" id=""10352""><form id=""laas"" lang=""ceb"">laas</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; c]</itype><trans> take a sword or rod and raise it. </trans>
<eg><i lang=""ceb"">Naglaas ug sundang ang hurimintádu,</i><trans> The amok was brandishing a bolo. </trans></eg>
<eg><i lang=""ceb"">Gilaas sa tigúlang ang íyang ulísi,</i><trans> The old man raised his cane in the air. </trans></eg>
</sense>
