INSERT INTO `wced_entry` VALUES (10355, "laay", "537a", "<entry page=""537a"" id=""10355""><form id=""laxay"" lang=""ceb"">láay</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> <tr>monotonous</tr>, wearingly <tr>boring</tr>. </trans>
<eg><i lang=""ceb"">Láay kaáyu ning wà tay búhat,</i><trans> It is so dull to be without anything to do. </trans></eg>
<eg><i lang=""ceb"">Láay kaáyu ang pagpaábut,</i><trans> It is very boring just to wait around. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> disgusting, disappointing in that one doesn’t conform to what is expected of him. </trans>
<eg><i lang=""ceb"">Laáya nímu uy, ingun ka palitan ku nímug sapátus,</i><trans> How disgusting! You promised to buy shoes for me (but you didn’t). </trans></eg>
<eg><i lang=""ceb"">Kaláay bayang Nína, gipanabì diay níya ang ákung sikrítu,</i><trans> How disgusting of Nina. She told my secret to everybody. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B124; b4]</itype><trans> be boring, feel bored. </trans>
<eg><i lang=""ceb"">Nagkalaay na ning trabahúa kay mu ra gihápun,</i><trans> This work is getting boring because it is always the same thing. </trans></eg>
<eg><i lang=""ceb"">Mipaúlì na lang siya kay gilaáyan nag pinaábut nímu,</i><trans> He just went home. He got tired of waiting for you. </trans></eg>
</sense></hom>
<entry><form id=""laay"" lang=""ceb""><abbr expan=""laay"">(→)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> a variety of corn that takes a long time to mature. </trans>
</sense></hom></entry>
<entry><form id=""laaylaay"" lang=""ceb"">laaylaay</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b(1)]</itype><trans> while away time or do s.t. for the meantime. </trans>
<eg><i lang=""ceb"">Samtang magpaábut ku mulaaylaay lang kug gansilyu,</i><trans> While I’m waiting, I’ll while away the time crocheting. </trans></eg>
<eg><i lang=""ceb"">Gilaaylaayan ku lang nig búhat, nahuman na giyud,</i><trans> I just did this little by little during <pb n=""538""/>my spare time, and now it’s finished. </trans></eg>
</sense></hom></entry>
<entry><form id=""kalaay"" lang=""ceb""><abbr expan=""kalaay"">ka-(→)</abbr></form>
<hom>
<sense n=""0""><trans> <xr lang=""ceb"" target=""#laaylaay"">= <sc>laaylaay</sc>.</xr> </trans>
</sense></hom></entry>
<entry><form id=""kalaxay"" lang=""ceb""><abbr expan=""kaláay"">ka-</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>boredom</tr>.
</trans>
</sense></hom></entry></entry>");
