INSERT INTO `wced_entry` VALUES (10358, "laba", "538", "<entry page=""538"" id=""10358""><form id=""laba"" lang=""ceb"">laba</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b1]</itype><trans> <tr>wash clothing</tr>. </trans>
<eg><i lang=""ceb"">Gilabhan níya ang ákung púlu,</i><trans> She washed my shirt. </trans></eg>
<eg><i lang=""ceb"">Ang batu nga ílang gilabhan,</i><trans> The stone where they do their washing. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A; b]</itype><trans> scold, talk against. </trans>
<eg><i lang=""ceb"">Gilabhan ka na sad sa ímung bus?</i><trans> Did your boss give you a cussing out again? </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[b(1)]</itype><trans> be badly defeated. </trans>
<eg><i lang=""ceb"">Gilabhan námù ang ílang tím sa baskitbul,</i><trans> We trounced their team in basketball. </trans></eg>
</sense></hom>
<entry><form id=""labakaxra"" lang=""ceb""><abbr expan=""labakára"">-kára</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>washcloth</tr>. </trans>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b]</itype><trans> wash the face with a washcloth. </trans>
</sense></hom></entry>
<entry><form id=""labalaxba"" lang=""ceb"">labalába</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; bc1]</itype><trans> <tr>work furiously</tr>, in a concentrated way. </trans>
<eg><i lang=""ceb"">Átung labalabáhan pagpanday ang iskuylahan,</i><trans> Let’s work furiously building the school. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> keep scolding, ranting against. </trans>
</sense></hom>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> done at a furious pace. </trans>
</sense></hom></entry>
<entry><form id=""labhanan"" lang=""ceb"">labhanan</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> dirty laundry. </trans>
</sense>
<sense n=""2""><number>2</number><trans> place to do wash. </trans>
</sense>
<sense n=""3""><number>3</number><trans> place to air grievances. </trans>
