INSERT INTO `wced_entry` VALUES (10359, "laba1", "538", "<entry page=""538"" id=""10359""><form id=""labaq"" lang=""ceb"">labà<sub>1</sub></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>stains</tr>, <tr>spots</tr>, uneven color. </trans>
<eg><i lang=""ceb"">Ang labà sa íyang mga sinínà walà makúhà,</i><trans> We could not get the stains out of her dress. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B; c1]</itype><trans> get stained, spotted in color. </trans>
<eg><i lang=""ceb"">Mulabà (malabà) ang kulur sa sinínà ug ibuwad kini sa ínit,</i><trans> The color of the dress will become uneven if it gets exposed to the sun. </trans></eg>
<eg><i lang=""ceb"">Ayaw nag labaa (ilabà) ug pintal,</i><trans> Don’t paint it unevenly. </trans></eg>
</sense></hom>
<entry><form id=""labaqlabaq"" lang=""ceb"">labàlabà</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B46]</itype><trans> have splotches of lighter or darker hue, not evenly smooth. </trans>
<eg><i lang=""ceb"">Naglabàlabà ang sinínang dikulur nga giladlad,</i><trans> The colored dress got splotched when it was bleached in the sun.
</trans></eg>
</sense></hom></entry></entry>");
