INSERT INTO `wced_entry` VALUES (10380, "labang", "539", "<entry page=""539"" id=""10380""><form id=""labang"" lang=""ceb"">labang</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; b5c]</itype><trans> <tr>cross</tr> s.t., go across, bring across. </trans>
<eg><i lang=""ceb"">Mulabang na ta, rid láyit na,</i><trans> Let’s cross now. It’s red light. </trans></eg>
<eg><i lang=""ceb"">Labangun pa nímu ang pitu ka lawud úsà ku nímu maangkun,</i><trans> You cannot have me (lit. you have to cross seven seas before you can possess me). </trans></eg>
<eg><i lang=""ceb"">Ilabang siya ngadtu sa píkas,</i><trans> Bring her across the street. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[A2; b8]</itype><trans> <tr>pass examinations</tr>. </trans>
<eg><i lang=""ceb"">Gamay ra ang milabang sa pasúlit sa pagkaduktur,</i><trans> Only a few people passed the doctor’s examination. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; b5]</itype><trans> come through a crisis. </trans>
<eg><i lang=""ceb"">Labangun (labangan) pa nímu ang túmang kaguul,</i><trans> You will have to pass through great sorrow. </trans></eg>
</sense>
<sense n=""4""><number>4</number> <itype>[A; b]</itype><trans> <tr>surpass</tr>. </trans>
<eg><i lang=""ceb"">Labangan pa kunu níya ang ríkurd ni Sulayman,</i><trans> She says she will beat Sulaiman’s record. </trans></eg>
<eg><i lang=""ceb"">Ayaw ilabang sa napúlù ang ímung pagkúhà,</i><trans> Don’t take more than ten. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> place across from s.t. </trans>
<eg><i lang=""ceb"">Ang ílang baul náa sa labang sa sapà,</i><trans> Their field is across the river. </trans></eg>
</sense></hom>
<entry><form id=""labang_pa_labang_sa"" lang=""ceb""><abbr expan=""labang"">—</abbr> pa, <abbr expan=""labang sa"">sa</abbr></form>
<hom>
<sense n=""0""><trans> <tr>further than</tr>, <tr>more than</tr>. </trans>
<eg><i lang=""ceb"">Labang pa niánà ang ákung buhátun ug tagáan kug higayun,</i><trans> I’ll do more than that if I’m given the chance. </trans></eg>
</sense></hom></entry>
<entry><form id=""labang_labangun_ug_pitu_ka_lawud"" lang=""ceb""><abbr expan=""labang"">—</abbr>, <abbr expan=""labangun"">-un</abbr> ug pitu ka lawud</form>
<hom>
<sense n=""0""><trans> may it not happen, may it be impossible (lit. have to cross seven seas to commit). </trans>
<eg><i lang=""ceb"">Simbaku. Labangun (ilabang) ug pitu ka lawud nga mamatay,</i><trans> God forbid. Let it not happen that he die! </trans></eg>
</sense></hom></entry>
<entry><form id=""labangunun"" lang=""ceb""><abbr expan=""labangunun"">-unun(←)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> things one has to surmount, trials to which one is put. </trans>
</sense></hom></entry>
<entry><form id=""labangaxnan_labnganan"" lang=""ceb""><abbr expan=""labangánan"">-anan(←)</abbr>, labnganan</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> place one crosses.
</trans>
</sense></hom></entry></entry>");
