INSERT INTO `wced_entry` VALUES (10386, "lab-as", "540", "<entry page=""540"" id=""10386""><form id=""lab-as"" lang=""ceb"">lab-as</form>
<hom role=""a""><pos>a</pos>
<sense n=""1""><number>1</number><trans> for s.t. quickly perishable to be <tr>fresh</tr>. </trans>
