INSERT INTO `wced_entry` VALUES (10391, "labay", "541", "<entry page=""541"" id=""10391""><form id=""laxbay"" lang=""ceb"">lábay</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; ac]</itype><trans> <tr>throw</tr> at. </trans>
<eg><i lang=""ceb"">Labáyun tikawg hanggranid animála ka,</i><trans> I’ll throw a hand grenade at you, you beast. </trans></eg>
<eg><i lang=""ceb"">Labáyi tag duha ka buuk bi,</i><trans> Throw me down a couple. </trans></eg>
<eg><i lang=""ceb"">Ilábay kanákù ang búla,</i><trans> Throw me the ball. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number> <itype>[A; c]</itype><trans> <tr>throw away</tr>. </trans>
<eg><i lang=""ceb"">Ilábay ang mga latà,</i><trans> Throw away the spoiled fruits. </trans></eg>
</sense>
<sense n=""1b""><number>1b</number><trans> throw, discard in a card game. </trans>
</sense>
<sense n=""2""><number>2</number> <itype>[A; c]</itype><trans> assign to a remote place. </trans>
<eg><i lang=""ceb"">Way láin makalábay sa mga impliyádu ngadtu sa layù gawas sa supirintindinti,</i><trans> No one can assign the employees to a remote place except the superintendent. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> throw. </trans>
<eg><i lang=""ceb"">Kinsang lábay run?</i><trans> Whose turn is it to throw? </trans></eg>
<eg><i lang=""ceb"">Ang íyang lábay walà makaabut sa paril,</i><trans> His throw didn’t get as far as the wall. </trans></eg>
</sense></hom>
<entry><form id=""labayan"" lang=""ceb""><abbr expan=""labayan"">-an(→)</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> line drawn to indicate the place at which to stand in throwing s.t. </trans>
</sense></hom></entry>
<entry><form id=""labayanan"" lang=""ceb""><abbr expan=""labayanan"">-anan</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> dumping place. </trans>
<eg><i lang=""ceb"">Ang Pásil labayánan sa tanang húgaw sa syudad,</i><trans> Pasil is the place they dump all garbage in the city.
</trans></eg>
</sense></hom></entry></entry>");
