INSERT INTO `wced_entry` VALUES (10393, "labha", "541", "<entry page=""541"" id=""10393""><form id=""labhaq"" lang=""ceb"">labhà</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[ABN; b4]</itype><trans> for the skin to get welts, bruises, or red splotches; cause it to do so. </trans>
