INSERT INTO `wced_entry` VALUES (10424, "labud", "544", "<entry page=""544"" id=""10424""><form id=""labud"" lang=""ceb"">labud</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>welt</tr>. </trans>
<eg><i lang=""ceb"">Dílà ra ang walay labud nímu,</i><trans> I’ll beat every inch of you. (Lit. only your tongue will escape welts.) </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> k.o. <corr sic=""milliped"">millipede</corr>, about <measure reg=""13 cm"">5″</measure> long and <measure reg=""2.5 cm"">an inch</measure> around, found in moist earth. The body is covered with a hard, black shell divided into thin segments which go around the body and overlap. It does not bite but secretes a clear yellow substance which burns the skin and may blind the eyes. </trans>
</sense>
<sense n=""3""><number>3</number><trans> lies designed to convince s.o. (slang). </trans>
<eg><i lang=""ceb"">Dì ku mutúu nímu kay labud nà,</i><trans> I don’t believe you, because that is nothing more than big talk. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[BN; b4N]</itype><trans> get welts on. </trans>
<eg><i lang=""ceb"">Nanglabud (gipalabdan) ang ákung bukubuku,</i><trans> My back has welts all over it. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[a4]</itype><trans> infested with <corr sic=""millipeds"">millipedes</corr>. </trans>
<eg><i lang=""ceb"">Gilabud ang sílung sa ámung balay,</i><trans> The area under our house is full of millipeds. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[A; a]</itype><trans> convince s.o. with lies (slang). </trans>
<eg><i lang=""ceb"">Nakalabud siya sa íyang asáwa nga pasurúyun,</i><trans> He told his wife some tall stories so she would let him go out.
</trans></eg>
</sense></hom></entry>");
