INSERT INTO `wced_entry` VALUES (10481, "lagduk", "549", "<entry page=""549"" id=""10481""><form id=""lagduk"" lang=""ceb"">lagduk</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> a small <tr>stake</tr>. </trans>
