INSERT INTO `wced_entry` VALUES (10578, "lahi", "555", "<entry page=""555"" id=""10578""><form id=""lahiq"" lang=""ceb"">lahì</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> different. </trans>
<eg><i lang=""ceb"">Káda táwu lahì sa usa,</i><trans> Each person is different from every other. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[BC; ac]</itype><trans> be different or dissimilar. </trans>
<eg><i lang=""ceb"">Malahì giyud kag muadtu kag magdyins,</i><trans> You’ll surely be different from everybody else if you go there in jeans. </trans></eg>
<eg><i lang=""ceb"">Nagláhì ang ílang hitsúra bisag kalúha sila,</i><trans> They look different even though they are twins. </trans></eg>
<eg><i lang=""ceb"">Ilahì (lahia) ang pagkáun sa mga binatunan sa átù,</i><trans> Give the servants different food from ours. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[AC12]</itype><trans> do s.t. apart from others or the main group. </trans>
<eg><i lang=""ceb"">Mulahì sila sa grúpu inigpangáun na,</i><trans> They eat in a small group off to themselves. </trans></eg>
</sense></hom>
<entry><form id=""palahiq"" lang=""ceb""><abbr expan=""palahì"">pa-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; a]</itype><trans> do s.t. different from the ordinary. </trans>
<eg><i lang=""ceb"">Dílì ku manundug, mupalahì giyud ku,</i><trans> I won’t imitate, I’ll do s.t. different. </trans></eg>
</sense></hom></entry>
<entry><form id=""lahiqlaxhiq"" lang=""ceb"">lahìláhì</form>
<hom role=""a""><pos>a</pos>
<sense n=""0""><trans> of different kinds. </trans>
<eg><i lang=""ceb"">Lahìláhing mga panaptun ang íyang gipalit,</i><trans> She bought different kinds of cloth. </trans></eg>
</sense></hom>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B16; a]</itype><trans> be of different kinds. </trans>
</sense></hom></entry>
<entry><form id=""kalahiq"" lang=""ceb""><abbr expan=""kalahì"">ka-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[B1456]</itype><trans> be of various kinds. </trans>
<eg><i lang=""ceb"">Nagkalahìláhì ang ámung upiniyun,</i><trans> Our opinions were varied.
</trans></eg>
</sense></hom></entry></entry>");
