INSERT INTO `wced_entry` VALUES (10590, "lahutay", "556", "<entry page=""556"" id=""10590""><form id=""lahuxtay"" lang=""ceb"">lahútay</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; c]</itype><trans> continue doing s.t. one has started, endure to continue s.t. </trans>
<eg><i lang=""ceb"">Mulahútay kug isturya hangtud sa kaadláwun,</i><trans> I can go on talking till dawn. </trans></eg>
<eg><i lang=""ceb"">Mulahútay ba ang ílang panagdáit hangtud mahuman ang pruyiktu?</i><trans> Will their good feelings for each other endure to the end of the project? </trans></eg>
<eg><i lang=""ceb"">Dílì makalahútay sa trabáhu ning ákung láwas,</i><trans> My body cannot endure to keep on with the work. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> action of persevering. </trans>
<eg><i lang=""ceb"">Bilib ku sa íyang lahútay nga musimánag bilar nga way pupahúway,</i><trans> I take off my hat to his ability to continue keeping vigil for a week without rest.
</trans></eg>
</sense></hom></entry>");
