INSERT INTO `wced_entry` VALUES (10662, "lam-ang1", "565", "<entry page=""565"" id=""10662""><form id=""lam-ang"" lang=""ceb"">lam-ang<sub>1</sub></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b4]</itype><trans> </trans>
</sense>
<sense n=""1""><number>1</number><trans> overstep, go over s.t. </trans>
